    @OnMessage
    public void echoBinary(ByteBuffer data, Session session) {
        logger.log(Level.INFO, "Received binary message of length: {0}", data.remaining());
        try {
            session.getBasicRemote().sendBinary(data);
        } catch (IOException e) {
            logger.log(Level.SEVERE, "Error sending binary message", e);
        }
    }
}

