import javax.websocket.OnError;
import javax.websocket.OnOpen;
import javax.websocket.Session;
import javax.websocket.server.PathParam;
import javax.websocket.server.ServerEndpoint;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

@Component
@ServerEndpoint(value = "/container/deploy/{id}", configurator = SpringConfigurator.class)
public class ContainerDeployServerEndpoint {

    @Autowired
    private ContainerService containerService;

    private static final Logger log = LoggerFactory.getLogger(ContainerDeployServerEndpoint.class);

    @OnOpen
    public void onOpen(@PathParam("id") Long id, Session session) {
        try {
            session.getBasicRemote().sendText("Connection established for container deployment: " + id);
            containerService.deployContainer(id);
            session.getBasicRemote().sendText("Deployment initiated for container: " + id);
        } catch (Exception e) {
            log.error("Deployment failed for container: " + id, e);
            try {
                session.getBasicRemote().sendText("Deployment failed for container: " + id);
            } catch (Exception ex) {
                log.error("Error sending failure message for container: " + id, ex);
            }
        } finally {
            try {
                session.close();
            } catch (Exception e) {
                log.error("Error closing session for container: " + id, e);
            }
        }
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        log.error("WebSocket error", throwable);
        try {
            session.close();
        } catch (Exception e) {
            log.error("Error closing session after WebSocket error", e);
        }
    }
}

