public class HttpUtil {
    private static final int TIMEOUT = 5000;
    private static final String DEFAULT_ENCODING = "UTF-8";
    private static final Map<String, String> currentProxy = null;

    private static final HostnameVerifier allHostsValid = (hostname, session) -> true;

    public static String httpRequest(String requestUrl, int timeOut, String requestMethod, String contentType, String postString, String encoding) throws IOException {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(requestUrl);
            connection = (HttpURLConnection) url.openConnection();
            if (connection instanceof HttpsURLConnection) {
                ((HttpsURLConnection) connection).setSSLSocketFactory(createSslSocketFactory());
                ((HttpsURLConnection) connection).setHostnameVerifier(allHostsValid);
            }
            connection.setConnectTimeout(timeOut);
            connection.setReadTimeout(timeOut);
            connection.setRequestMethod(requestMethod);
            connection.setRequestProperty("Content-Type", contentType);
            connection.setDoInput(true);
            if ("POST".equalsIgnoreCase(requestMethod) && postString != null) {
                connection.setDoOutput(true);
                try (OutputStream os = connection.getOutputStream()) {
                    os.write(postString.getBytes(encoding));
                }
            }
            int responseCode = connection.getResponseCode();
            InputStream inputStream = responseCode >= 200 && responseCode < 300 ? connection.getInputStream() : connection.getErrorStream();
            return readString(inputStream, encoding);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private static String readString(InputStream inputStream, String encoding) throws IOException {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream, encoding))) {
