        if (stubProxy.getBlockingStub() == null) {
            synchronized (this) {
                if (stubProxy.getBlockingStub() == null) {
                    String host = resetStub();
                    if (host.isEmpty()) {
                        throw new PDException(Pdpb.ErrorType.PD_UNREACHABLE_VALUE,
                                              "PD unreachable, pd.peers=" +
                                              config.getServerHost());
