import io.grpc.stub.StreamObserver;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;
import java.util.function.Predicate;

public abstract class AbstractClient {
    private final ConcurrentHashMap<String, ManagedChannel> chs = new ConcurrentHashMap<>();
    private final AbstractClientStubProxy stubProxy;
    private final PDConfig config;
    private ManagedChannel channel;
    private final ConcurrentMap<String, AbstractBlockingStub> stubs = new ConcurrentHashMap<>();

    public AbstractClient(PDConfig config) {
