        this.stubProxy = new AbstractClientStubProxy();
        resetStub();
    }

    public AbstractBlockingStub getBlockingStub() {
        String leaderHost = resetStub();
        return stubs.computeIfAbsent(leaderHost, this::createBlockingStub);
    }

    public AbstractStub getStub() {
        return stubProxy.getStub();
    }

    protected abstract AbstractStub createStub();

    protected abstract AbstractBlockingStub createBlockingStub();

    private String resetStub() {
        String leaderHost = findLeaderHost();
        channel = chs.computeIfAbsent(leaderHost, this::createChannel);
        stubProxy.setStub(createStub());
        return leaderHost;
    }

    private ManagedChannel createChannel(String host) {
        return ManagedChannelBuilder.forTarget(host)
                .usePlaintext()
                .build();
    }

    private String findLeaderHost() {
        // Logic to determine the current leader host
        return config.getLeaderHost();
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req) {
        return blockingUnaryCall(method, req, config.getDefaultRetry());
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req, int retry) {
        for (int i = 0; i < retry; i++) {
            try {
                return getBlockingStub().blockingUnaryCall(method, req);
            } catch (StatusRuntimeException e) {
                if (i == retry - 1 || !isRetryable(e)) {
                    throw e;
                }
                resetStub();
            }
        }
        throw new RuntimeException("Failed to execute blockingUnaryCall");
    }

    public <ReqT, RespT> KVPair<Boolean, RespT> concurrentBlockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req, Predicate<RespT> predicate) {
        for (String host : config.getHosts()) {
            ManagedChannel ch = chs.computeIfAbsent(host, this::createChannel);
            AbstractBlockingStub stub = createBlockingStub(ch);
            try {
                RespT response = stub.blockingUnaryCall(method, req);
                if (predicate.test(response)) {
                    return new KVPair<>(true, response);
                }
            } catch (StatusRuntimeException ignored) {
            }
        }
        return new KVPair<>(false, null);
    }

    public <ReqT, RespT> void streamingCall(MethodDescriptor<ReqT, RespT> method, ReqT request, StreamObserver<RespT> responseObserver, int retry) {
        for (int i = 0; i < retry; i++) {
            try {
                getStub().streamingCall(method, request, responseObserver);
                return;
            } catch (StatusRuntimeException e) {
                if (i == retry - 1 || !isRetryable(e)) {
                    throw e;
                }
                resetStub();
            }
        }
    }

    public void close() {
        chs.values().forEach(this::closeChannel);
        chs.clear();
    }

    private void closeChannel(ManagedChannel channel) {
        if (channel != null && !channel.isShutdown()) {
            channel.shutdown();
        }
    }

    private boolean isRetryable(StatusRuntimeException e) {
        return e.getStatus().getCode() == Status.Code.UNAVAILABLE;
    }
}

