import io.grpc.BindableService;
import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.ServerInterceptor;
import io.grpc.ServiceDescriptor;
import io.grpc.MethodDescriptor;
import java.lang.reflect.Method;
import java.util.List;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

public class GrpcServer {
    private Server server;
    private final Map<String, Class<? extends BindableService>> serviceNameMap = new ConcurrentHashMap<>();
    private final Map<String, Method> fullMethodNameMap = new ConcurrentHashMap<>();

    public GrpcServer(GrpcServerProperties properties, List<ServerInterceptor> interceptors, List<BindableService> services) {
        this(ServerBuilder.forPort(properties.getPort()), properties, interceptors, services);
    }

    public GrpcServer(ServerBuilder<?> builder, GrpcServerProperties properties, List<ServerInterceptor> interceptors, List<BindableService> services) {
        for (ServerInterceptor interceptor : interceptors) {
            builder.intercept(interceptor);
        }
        for (BindableService service : services) {
            builder.addService(service);
            registerService(service);
        }
        this.server = builder.build();
    }

    private void registerService(BindableService service) {
        ServiceDescriptor descriptor = service.bindService().getServiceDescriptor();
        serviceNameMap.put(descriptor.getName(), service.getClass());
        for (MethodDescriptor<?, ?> methodDescriptor : descriptor.getMethods()) {
            Method method = resolve(methodDescriptor, service.getClass());
            fullMethodNameMap.put(methodDescriptor.getFullMethodName(), method);
        }
    }

    public boolean isRunning() {
        return server != null && !server.isShutdown();
    }

    public int port() {
        return server.getPort();
    }

    public Class<? extends BindableService> findClass(ServiceDescriptor descriptor) {
        return serviceNameMap.get(descriptor.getName());
    }

    public Method findMethod(MethodDescriptor<?, ?> descriptor) {
        return fullMethodNameMap.get(descriptor.getFullMethodName());
    }

    public Method resolve(MethodDescriptor<?, ?> descriptor, Class<? extends BindableService> cls) {
        try {
            return cls.getMethod(descriptor.getBareMethodName());
        } catch (NoSuchMethodException e) {
            throw new RuntimeException("Method not found: " + descriptor.getBareMethodName(), e);
        }
    }

    public void onApplicationStart() {
        try {
            server.start();
            server.awaitTermination();
        } catch (Exception e) {
            throw new RuntimeException("Failed to start gRPC server", e);
        }
    }

    public void onApplicationStop() {
        if (server != null) {
            server.shutdown();
        }
    }
}

class GrpcServerProperties {
    private int port;

    public int getPort() {
        return port;
    }

    public void setPort(int port) {
        this.port = port;
    }
}

