import org.springframework.stereotype.Component;

import javax.annotation.PreDestroy;
import java.io.IOException;
import java.util.Map;

@Slf4j
@Component
public class GrpcLauncher {

    private Server server;

    @Value("${grpc.server.port}")
    private Integer grpcServerPort;

    private static final int DEFAULT_MAX_MESSAGE_SIZE = 10 * 1024 * 1024; // 10MB

    public void grpcStart(Map<String, Object> grpcServiceBeanMap) throws IOException {
        ServerBuilder<?> serverBuilder = ServerBuilder.forPort(grpcServerPort)
                .maxInboundMessageSize(DEFAULT_MAX_MESSAGE_SIZE);

        grpcServiceBeanMap.forEach((name, service) -> {
            serverBuilder.addService((io.grpc.BindableService) service);
            log.info("Registered gRPC service: {}", name);
        });

        server = serverBuilder.build().start();
        log.info("gRPC server started on port: {}", grpcServerPort);

        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            log.info("JVM shutdown detected, stopping gRPC server...");
            grpcStop();
            log.info("gRPC server stopped.");
        }));
    }

    @PreDestroy
    public void grpcStop() {
        if (server != null) {
            server.shutdown();
            log.info("gRPC server is shutting down...");
            try {
                server.awaitTermination();
            } catch (InterruptedException e) {
                log.error("gRPC server termination interrupted", e);
            }
            log.info("gRPC server has been shut down.");
        }
    }
}

