        public void get(DataRequest request, StreamObserver<DataResponse> responseObserver) {
            syncRequests.inc();
            String responseMessage = request.getMessage().toUpperCase();
            DataResponse response = DataResponse.newBuilder().setMessage(responseMessage).build();
            responseObserver.onNext(response);
            responseObserver.onCompleted();
        }

        @Override
        public void streamingGet(EmptyRequest request, StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            for (int i = 0; i < 10; i++) {
                DataResponse response = DataResponse.newBuilder().setMessage("Response " + i).build();
                responseObserver.onNext(response);
            }
            responseObserver.onCompleted();
        }

        @Override
        public StreamObserver<DataRequest> streamingPut(StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            return new StreamObserver<DataRequest>() {
                @Override
                public void onNext(DataRequest request) {
                    String modifiedMessage = "Processed: " + request.getMessage();
                    DataResponse response = DataResponse.newBuilder().setMessage(modifiedMessage).build();
                    responseObserver.onNext(response);
                }

                @Override
                public void onError(Throwable t) {
                    LOG.error("Error in streamingPut", t);
                }

                @Override
                public void onCompleted() {
                    responseObserver.onCompleted();
                }
            };
        }
    }
