import java.io.*;
import java.net.*;
import java.util.logging.*;

public class TcpTransport {
    private ServerSocket srv_sock;
    private Socket sock;
    private OutputStream output;
    private InputStream input;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private int out_buf_size = 8192;
    private int in_buf_size = 8192;
    private boolean server;
    private boolean tcp_nodelay;
    private Logger log = Logger.getLogger(TcpTransport.class.getName());

    public TcpTransport(RtReceiver receiver, InetAddress host, int port, boolean server, boolean tcp_nodelay) {
        this.receiver = receiver;
        this.host = host;
        this.port = port;
        this.server = server;
        this.tcp_nodelay = tcp_nodelay;
    }

    public void start(String... options) throws IOException {
        if (server) {
            srv_sock = new ServerSocket(port);
            log.info("Server started on port " + port);
            sock = srv_sock.accept();
            log.info("Client connected");
        } else {
            sock = new Socket(host, port);
            log.info("Connected to server " + host + ":" + port);
        }
        sock.setTcpNoDelay(tcp_nodelay);
        output = new BufferedOutputStream(sock.getOutputStream(), out_buf_size);
        input = new BufferedInputStream(sock.getInputStream(), in_buf_size);
        receiver_thread = new Receiver(input);
        receiver_thread.start();
    }

    public void stop() {
        try {
            if (receiver_thread != null) {
                receiver_thread.interrupt();
            }
            if (input != null) {
                input.close();
            }
            if (output != null) {
                output.close();
            }
            if (sock != null) {
                sock.close();
            }
            if (srv_sock != null) {
                srv_sock.close();
            }
            log.info("Transport stopped");
        } catch (IOException e) {
            log.log(Level.SEVERE, "Error stopping transport", e);
        }
    }

    public void send(byte[] buf, int offset, int length) throws IOException {
        if (output != null) {
            output.write(buf, offset, length);
            output.flush();
        }
    }

    private class Receiver extends Thread {
        private InputStream in;

        public Receiver(InputStream in) {
            this.in = in;
        }

        public void run() {
            byte[] buffer = new byte[in_buf_size];
            try {
                int bytesRead;
                while (!Thread.currentThread().isInterrupted() && (bytesRead = in.read(buffer)) != -1) {
                    receiver.onReceive(buffer, 0, bytesRead);
                }
            } catch (IOException e) {
                log.log(Level.SEVERE, "Error in receiver thread", e);
            }
        }
    }

    public interface RtReceiver {
        void onReceive(byte[] data, int offset, int length);
    }
}

