    public HttpMediaStreamer(String filename, String mimeType) throws IOException {
        this.uri = create(filename, mimeType);
    }

    public Uri getUri() {
        return uri;
    }

    public void destroy() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
            if (executorService != null && !executorService.isShutdown()) {
                executorService.shutdownNow();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private Uri create(String filename, String mimeType) throws IOException {
        serverSocket = new ServerSocket(0);
        int port = serverSocket.getLocalPort();
        executorService = Executors.newSingleThreadExecutor();
        executorService.submit(() -> {
            try {
                while (!serverSocket.isClosed()) {
                    Socket clientSocket = serverSocket.accept();
                    handleClient(clientSocket, filename, mimeType);
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
        return new Uri("http://localhost:" + port + "/" + filename);
    }

    private void handleClient(Socket clientSocket, String filename, String mimeType) {
