import java.io.*;
import java.net.*;
import java.util.concurrent.*;

public class JankyProxy {
    private ThreadProxy thread;
    private ServerSocket socket;
    private final String SERVER_URL;
    private final int SERVER_PORT;
    private boolean wasUsedForClientToServer = false;

    public JankyProxy(String serverUrl, int serverPort) {
        this.SERVER_URL = serverUrl;
        this.SERVER_PORT = serverPort;
    }

    public void runServer(String host, int remoteport, int localport) throws IOException {
        socket = new ServerSocket(localport);
        System.out.println("Proxy started on port " + localport);
        while (true) {
            Socket clientSocket = socket.accept();
            thread = new ThreadProxy(clientSocket, SERVER_URL, SERVER_PORT);
            new Thread(thread).start();
        }
    }

    public boolean wasUsed() {
        return wasUsedForClientToServer;
    }

    public void shutdown() throws IOException {
        if (thread != null) {
            thread.shutdown();
        }
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }

    private class ThreadProxy implements Runnable {
        private final Socket clientSocket;
        private final String serverUrl;
        private final int serverPort;
        private Socket serverSocket;
        private ExecutorService executor = Executors.newFixedThreadPool(2);

        public ThreadProxy(Socket clientSocket, String serverUrl, int serverPort) {
            this.clientSocket = clientSocket;
            this.serverUrl = serverUrl;
            this.serverPort = serverPort;
        }

        @Override
        public void run() {
            try {
                serverSocket = new Socket(serverUrl, serverPort);
                executor.execute(() -> forwardData(clientSocket, serverSocket));
                executor.execute(() -> forwardData(serverSocket, clientSocket));
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        private void forwardData(Socket inputSocket, Socket outputSocket) {
            try (InputStream input = inputSocket.getInputStream();
                 OutputStream output = outputSocket.getOutputStream()) {
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = input.read(buffer)) != -1) {
                    output.write(buffer, 0, bytesRead);
                    output.flush();
                    if (inputSocket == clientSocket) {
                        wasUsedForClientToServer = true;
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                shutdown();
            }
        }

        public void shutdown() {
            try {
                if (clientSocket != null && !clientSocket.isClosed()) {
                    clientSocket.close();
                }
                if (serverSocket != null && !serverSocket.isClosed()) {
                    serverSocket.close();
                }
                executor.shutdownNow();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

