import java.io.*;
import java.net.*;
import org.ietf.jgss.*;

public class GSSServer {
    private ServerSocket s;

    public static void main(String[] args) {
        String serviceName = null;
        int port = 0;
        Oid mechOid = null;

        for (int i = 0; i < args.length; i++) {
            if (args[i].equals("-port")) {
                port = Integer.parseInt(args[++i]);
            } else if (args[i].equals("-mech")) {
                try {
                    mechOid = new Oid(args[++i]);
                } catch (GSSException e) {
                    print("Invalid mechanism OID");
                    usage();
                    exit(1);
                }
            } else {
                serviceName = args[i];
            }
        }

        if (serviceName == null || port == 0 || mechOid == null) {
            usage();
            exit(1);
        }

        try {
            GSSManager manager = GSSManager.getInstance();
            GSSName serverName = manager.createName(serviceName, GSSName.NT_HOSTBASED_SERVICE);
            GSSCredential serverCreds = manager.createCredential(serverName, GSSCredential.INDEFINITE_LIFETIME, mechOid, GSSCredential.ACCEPT_ONLY);

            GSSServer server = new GSSServer();
            server.s = new ServerSocket(port);
            print("Server started on port " + port);

            while (true) {
                Socket client = server.s.accept();
                server.processClient(serverCreds, client);
            }
        } catch (Exception e) {
            print("Error: " + e.getMessage());
            exit(1);
        }
    }

    private void processClient(GSSCredential server, Socket client) {
        try {
            GSSManager manager = GSSManager.getInstance();
            GSSContext context = manager.createContext(server);

            DataInputStream dis = new DataInputStream(client.getInputStream());
            DataOutputStream dos = new DataOutputStream(client.getOutputStream());

            byte[] token = new byte[dis.readInt()];
            dis.readFully(token);
            context.acceptSecContext(token, 0, token.length);

            if (!context.isEstablished()) {
                print("Failed to establish context");
                client.close();
                return;
            }

            exchangeWithPeer(context, dis, dos);
            context.dispose();
            client.close();
        } catch (Exception e) {
            print("Error processing client: " + e.getMessage());
        }
    }

    private void exchangeWithPeer(GSSContext aCtxt, DataInputStream dis, DataOutputStream dos) {
        try {
            byte[] message = new byte[dis.readInt()];
            dis.readFully(message);

            byte[] response = aCtxt.unwrap(message, 0, message.length, null);
            dos.writeInt(response.length);
            dos.write(response);
        } catch (Exception e) {
            print("Error exchanging messages: " + e.getMessage());
        }
    }

    private static void usage() {
        print("Usage: java GSSServer -port [portNumber] -mech [mechanismOID] serviceName");
    }

    private static void print(String msg) {
        System.out.println(msg);
    }

    private static void exit(int status) {
        System.exit(status);
    }
}

