import java.io.*;
import java.net.*;
import java.util.*;
import java.util.concurrent.*;

public class NetCat {
    private int port;
    private ServerSocket server;
    private Socket client;
    private ExecutorService pool;
    private OutputStream os;
    private PrintWriter pw;

    public NetCat(int port) {
        this.port = port;
    }

    public boolean check(Map<String, Object> param) {
        Integer port = (Integer) param.get("port");
        return port != null && port >= 10000;
    }

    public void before() throws IOException {
        server = new ServerSocket(port);
        pool = Executors.newFixedThreadPool(2);
        System.out.println("Server started on port: " + port);
    }

    public void listen() throws IOException {
        client = server.accept();
        os = client.getOutputStream();
        pw = new PrintWriter(os, true);
        System.out.println("Client connected: " + client.getInetAddress());
    }

    public void echo() {
        pool.execute(() -> {
            try (BufferedReader br = new BufferedReader(new InputStreamReader(client.getInputStream()))) {
                String line;
                while ((line = br.readLine()) != null) {
                    System.out.println("Received: " + line);
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
    }

    public Object start() {
        pool.execute(() -> {
            try (BufferedReader userInput = new BufferedReader(new InputStreamReader(System.in))) {
                String input;
                while ((input = userInput.readLine()) != null) {
                    pw.println(input);
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
        return null;
    }

    public void onClose() {
        try {
            if (client != null) client.close();
            if (server != null) server.close();
            if (pool != null) pool.shutdown();
            System.out.println("Server closed.");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        int port = 10000; // Example port
        NetCat netCat = new NetCat(port);
        Map<String, Object> param = new HashMap<>();
        param.put("port", port);

        if (netCat.check(param)) {
            try {
                netCat.before();
                netCat.listen();
                netCat.echo();
                netCat.start();
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                netCat.onClose();
            }
        } else {
            System.out.println("Invalid port number. Please use a port >= 10000.");
        }
    }
}

