import java.io.*;
import java.net.*;
import java.nio.ByteBuffer;
import java.security.MessageDigest;
import java.util.Base64;

public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        System.out.println("WebSocket server started on port " + port);
        while (true) {
            Socket clientSocket = serverSocket.accept();
            new Thread(() -> {
                try {
                    handleConnection(clientSocket);
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }).start();
        }
    }

    private void handleConnection(Socket clientSocket) throws IOException {
        InputStream input = clientSocket.getInputStream();
        OutputStream output = clientSocket.getOutputStream();
        BufferedReader reader = new BufferedReader(new InputStreamReader(input));

        String line;
        String webSocketKey = null;
        while (!(line = reader.readLine()).isEmpty()) {
            if (line.startsWith("Sec-WebSocket-Key: ")) {
                webSocketKey = line.substring(19);
            }
        }

        if (webSocketKey != null) {
            String acceptKey = generateAcceptKey(webSocketKey);
            String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                              "Upgrade: websocket\r\n" +
                              "Connection: Upgrade\r\n" +
                              "Sec-WebSocket-Accept: " + acceptKey + "\r\n\r\n";
            output.write(response.getBytes("UTF-8"));
            sendBinaryFrame(output, new byte[]{0x01, 0x02, 0x03, 0x04}, false);
        }

        clientSocket.close();
    }

    private String generateAcceptKey(String webSocketKey) throws IOException {
        try {
            String key = webSocketKey + "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
            MessageDigest md = MessageDigest.getInstance("SHA-1");
            byte[] hash = md.digest(key.getBytes("UTF-8"));
            return Base64.getEncoder().encodeToString(hash);
        } catch (Exception e) {
            throw new IOException("Failed to generate accept key", e);
        }
    }

    private void sendBinaryFrame(OutputStream output, byte[] data, boolean masked) throws IOException {
        int frameSize = data.length;
        ByteBuffer frame = ByteBuffer.allocate(2 + frameSize + (masked ? 4 : 0));
        frame.put((byte) 0x82); // FIN and opcode for binary frame
        if (frameSize <= 125) {
            frame.put((byte) (masked ? 0x80 | frameSize : frameSize));
        } else if (frameSize <= 65535) {
            frame.put((byte) (masked ? 0xFE : 126));
            frame.putShort((short) frameSize);
        } else {
            frame.put((byte) (masked ? 0xFF : 127));
            frame.putLong(frameSize);
        }

        if (masked) {
            byte[] mask = new byte[4];
            new java.util.Random().nextBytes(mask);
            frame.put(mask);
            for (int i = 0; i < data.length; i++) {
                data[i] ^= mask[i % 4];
            }
        }

        frame.put(data);
        output.write(frame.array());
    }

    public void stop() throws IOException {
        if (serverSocket != null && !serverSocket.isClosed()) {
            serverSocket.close();
        }
    }

    public static void main(String[] args) {
        try {
            WebSocketServer server = new WebSocketServer(8080);
            server.start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

