    private final ApplicationInfoManager applicationInfoManager;
    private final EurekaClient eurekaClient;
    private ServerSocket serverSocket;
    private final int port = 8080;

    private ExampleServiceBase(ApplicationInfoManager applicationInfoManager, EurekaClient eurekaClient) {
        this.applicationInfoManager = applicationInfoManager;
        this.eurekaClient = eurekaClient;
    }

    public static synchronized ExampleServiceBase getInstance(ApplicationInfoManager applicationInfoManager, EurekaClient eurekaClient) {
        if (instance == null) {
            instance = new ExampleServiceBase(applicationInfoManager, eurekaClient);
        }
        return instance;
    }

    public void start() {
        try {
            applicationInfoManager.setInstanceStatus(InstanceStatus.UP);
            serverSocket = new ServerSocket(port);
            System.out.println("Server started on port " + port);

            while (true) {
                Socket clientSocket = serverSocket.accept();
                processRequest(clientSocket);
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            cleanup();
        }
    }

    private void processRequest(Socket clientSocket) {
        try (BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
             PrintWriter out = new PrintWriter(new OutputStreamWriter(clientSocket.getOutputStream()), true)) {

            String request = in.readLine();
