public class HttpUrlSource {
    private final SourceInfoStorage sourceInfoStorage;
    private final HeaderInjector headerInjector;
    private SourceInfo sourceInfo;
    private HttpURLConnection connection;
    private InputStream inputStream;

    public HttpUrlSource(SourceInfoStorage sourceInfoStorage, HeaderInjector headerInjector, String url) {
        this.sourceInfoStorage = sourceInfoStorage;
        this.headerInjector = headerInjector;
        this.sourceInfo = sourceInfoStorage.get(url);
        if (this.sourceInfo == null) {
            this.sourceInfo = new SourceInfo(url);
        }
    }

    public long length() throws IOException {
        if (sourceInfo.getLength() == -1) {
            fetchContentInfo();
        }
        return sourceInfo.getLength();
    }

    public void open(long offset) throws IOException {
        connection = openConnection(offset, 30000);
        inputStream = connection.getInputStream();
    }

    public void close() throws IOException {
        if (inputStream != null) {
            inputStream.close();
        }
        if (connection != null) {
            connection.disconnect();
        }
    }

    public int read(byte[] buffer) throws IOException {
        return inputStream.read(buffer);
    }

    public String getMime() throws IOException {
        if (sourceInfo.getMimeType() == null) {
            fetchContentInfo();
        }
        return sourceInfo.getMimeType();
    }

    public String getUrl() {
        return sourceInfo.getUrl();
    }

    private void fetchContentInfo() throws IOException {
        HttpURLConnection connection = openConnection(0, 5000);
        sourceInfo.setLength(getContentLength(connection));
        sourceInfo.setMimeType(connection.getContentType());
        sourceInfoStorage.put(sourceInfo);
        connection.disconnect();
    }

    private HttpURLConnection openConnection(long offset, int timeout) throws IOException {
        URL url = new URL(sourceInfo.getUrl());
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setConnectTimeout(timeout);
        connection.setReadTimeout(timeout);
        if (offset > 0) {
            connection.setRequestProperty("Range", "bytes=" + offset + "-");
        }
        injectCustomHeaders(connection, sourceInfo.getUrl());
        int responseCode = connection.getResponseCode();
        if (responseCode == HttpURLConnection.HTTP_MOVED_PERM || responseCode == HttpURLConnection.HTTP_MOVED_TEMP) {
            String newUrl = connection.getHeaderField("Location");
            connection.disconnect();
            sourceInfo.setUrl(newUrl);
            return openConnection(offset, timeout);
        }
        return connection;
    }

    private void injectCustomHeaders(HttpURLConnection connection, String url) {
        Map<String, String> headers = headerInjector.getHeaders(url);
        for (Map.Entry<String, String> entry : headers.entrySet()) {
            connection.setRequestProperty(entry.getKey(), entry.getValue());
        }
    }

    private long readSourceAvailableBytes(HttpURLConnection connection, long offset, int responseCode) throws IOException {
        if (responseCode == HttpURLConnection.HTTP_PARTIAL) {
            return getContentLength(connection);
        } else if (responseCode == HttpURLConnection.HTTP_OK) {
            return getContentLength(connection) - offset;
        } else {
            throw new IOException("Unexpected response code: " + responseCode);
        }
    }

    private long getContentLength(HttpURLConnection connection) {
        String contentLength = connection.getHeaderField("Content-Length");
        return contentLength != null ? Long.parseLong(contentLength) : -1;
    }
}

class SourceInfo {
    private String url;
    private long length;
    private String mimeType;

    public SourceInfo(String url) {
        this.url = url;
        this.length = -1;
        this.mimeType = null;
