import java.io.*;
import java.net.*;
import java.util.*;
import java.util.concurrent.*;

public abstract class EmbedHttpServer {
    private final int port;
    private ServerSocket serverSocket;
    private ExecutorService executor;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        executor = Executors.newCachedThreadPool();
        executor.submit(this::run);
    }

    public void stop() throws IOException {
        if (serverSocket != null && !serverSocket.isClosed()) {
            serverSocket.close();
        }
        if (executor != null) {
            executor.shutdown();
        }
    }

    private void run() {
        try {
            while (!serverSocket.isClosed()) {
                Socket clientSocket = serverSocket.accept();
                executor.submit(() -> handleClient(clientSocket));
            }
        } catch (IOException e) {
            if (!serverSocket.isClosed()) {
                e.printStackTrace();
            }
        }
    }

    private void handleClient(Socket clientSocket) {
        try (InputStream input = clientSocket.getInputStream();
             OutputStream output = clientSocket.getOutputStream()) {
            BufferedReader reader = new BufferedReader(new InputStreamReader(input));
            String requestLine = reader.readLine();
            if (requestLine == null || requestLine.isEmpty()) return;

            String[] requestParts = requestLine.split(" ");
            if (requestParts.length < 3) return;

            String method = requestParts[0];
            String path = requestParts[1];
            HashMap<String, String> headers = new HashMap<>();
            String line;
            while (!(line = reader.readLine()).isEmpty()) {
                int colonIndex = line.indexOf(":");
                if (colonIndex > 0) {
                    headers.put(line.substring(0, colonIndex).trim(), line.substring(colonIndex + 1).trim());
                }
            }

            Map<String, String> queries = parsePath(path);
            handle(method, path, headers, queries, input, output);
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    protected abstract void handle(String method, String path, HashMap<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response) throws IOException;

    protected Map<String, String> parsePath(String path) {
        Map<String, String> queryMap = new HashMap<>();
        int queryIndex = path.indexOf("?");
        if (queryIndex != -1) {
            String queryString = path.substring(queryIndex + 1);
            String[] queryPairs = queryString.split("&");
            for (String pair : queryPairs) {
                int equalsIndex = pair.indexOf("=");
                if (equalsIndex > 0) {
                    String key = pair.substring(0, equalsIndex);
                    String value = pair.substring(equalsIndex + 1);
                    queryMap.put(key, value);
                }
            }
        }
        return queryMap;
    }
}

