public class Webserver {
    private ServerSocket socket;
    private ClassPool classPool;
    private Translator translator;
    private static final byte[] endofline = "\r\n".getBytes();

    public Webserver(int port) throws IOException {
        socket = new ServerSocket(port);
    }

    public void setClassPool(ClassPool loader) {
        this.classPool = loader;
    }

    public void addTranslator(ClassPool cp, Translator t) {
        this.classPool = cp;
        this.translator = t;
    }

    public void end() throws IOException {
        socket.close();
    }

    public void run() {
        while (true) {
            try {
                Socket client = socket.accept();
                new Thread(() -> {
                    try {
                        process(client);
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void process(Socket clnt) throws IOException {
        InputStream in = clnt.getInputStream();
        OutputStream out = clnt.getOutputStream();
        String cmd = readLine(in);
        doReply(in, out, cmd);
        clnt.close();
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1 && c != '\n') {
            if (c != '\r') {
                sb.append((char) c);
            }
        }
        return sb.toString();
    }

    private int skipLine(InputStream in) throws IOException {
        int c;
        while ((c = in.read()) != -1 && c != '\n') {}
        return c;
    }

    private void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
        if (cmd.startsWith("GET")) {
            String filename = cmd.split(" ")[1];
            if (letUsersSendClassfile(out, filename, 0)) {
                return;
            }
            File file = new File(filename);
            if (file.exists()) {
                sendHeader(out, file.length(), 0);
                try (FileInputStream fis = new FileInputStream(file)) {
                    byte[] buffer = new byte[1024];
                    int bytesRead;
                    while ((bytesRead = fis.read(buffer)) != -1) {
                        out.write(buffer, 0, bytesRead);
                    }
                }
            } else {
                replyError(out, new BadHttpRequest("404 Not Found"));
            }
        } else {
            replyError(out, new BadHttpRequest("400 Bad Request"));
        }
    }

    private boolean letUsersSendClassfile(OutputStream out, String filename, int length) {
        try {
            CtClass cc = classPool.get(filename);
            byte[] bytecode = cc.toBytecode();
            sendHeader(out, bytecode.length, 1);
            out.write(bytecode);
            return true;
        } catch (Exception e) {
            return false;
        }
    }

    private void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
        out.write("HTTP/1.0 200 OK".getBytes());
        out.write(endofline);
        out.write(("Content-Length: " + dataLength).getBytes());
        out.write(endofline);
        out.write("Content-Type: ".getBytes());
        if (filetype == 0) {
            out.write("text/html".getBytes());
        } else {
            out.write("application/octet-stream".getBytes());
        }
        out.write(endofline);
        out.write(endofline);
    }

    private void replyError(OutputStream out, BadHttpRequest e) throws IOException {
        out.write(("HTTP/1.0 " + e.getMessage()).getBytes());
        out.write(endofline);
        out.write(endofline);
    }

    private static class BadHttpRequest extends Exception {
        public BadHttpRequest(String message) {
            super(message);
        }
    }
}

