import java.io.*;
import java.net.*;
import java.util.*;

public abstract class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private volatile boolean running = false;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        running = true;
        new Thread(this).start();
    }

    public void stop() throws IOException {
        running = false;
        if (serverSocket != null) {
            serverSocket.close();
        }
    }

    @Override
    public void run() {
        while (running) {
            try (Socket clientSocket = serverSocket.accept();
                 InputStream input = clientSocket.getInputStream();
                 OutputStream output = clientSocket.getOutputStream()) {

                BufferedReader reader = new BufferedReader(new InputStreamReader(input));
                String requestLine = reader.readLine();
                if (requestLine == null || requestLine.isEmpty()) {
                    continue;
                }

                StringTokenizer tokenizer = new StringTokenizer(requestLine);
                String method = tokenizer.nextToken();
                String path = tokenizer.nextToken();

                HashMap<String, String> headers = new HashMap<>();
                String line;
                while (!(line = reader.readLine()).isEmpty()) {
                    int idx = line.indexOf(':');
                    if (idx > 0) {
                        headers.put(line.substring(0, idx).trim(), line.substring(idx + 1).trim());
                    }
                }

                int contentLength = 0;
                if (headers.containsKey("Content-Length")) {
                    contentLength = Integer.parseInt(headers.get("Content-Length"));
                }

                InputStream bodyInput = new BodyInputStream(input, contentLength);
                handle(method, path, headers, bodyInput, output);

            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    protected abstract void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response) throws IOException;

    private static class BodyInputStream extends InputStream {
        private final InputStream ins;
        private int remaining;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.remaining = n;
        }

        @Override
        public int available() throws IOException {
            return remaining;
        }

        @Override
        public int read() throws IOException {
            if (remaining <= 0) {
                return -1;
            }
            int result = ins.read();
            if (result != -1) {
                remaining--;
            }
            return result;
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (remaining <= 0) {
                return -1;
            }
            len = Math.min(len, remaining);
            int result = ins.read(b, off, len);
            if (result != -1) {
                remaining -= result;
            }
            return result;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("Skip not supported");
        }

        @Override
        public void close() throws IOException {
            ins.close();
        }
    }
}

