        if (!isClosed.get()) {
            throw new IllegalStateException("Channel is already open");
        }
        socket = new Socket();
        socket.connect(new InetSocketAddress(dsc.getHost(), dsc.getPort()), (int) timeout);
        socket.setSoTimeout((int) timeout);
        in = new BufferedInputStream(socket.getInputStream());
        out = new BufferedOutputStream(socket.getOutputStream());
        isClosed.set(false);
    }

    public BinaryPacket execute(String statement) throws IOException {
        if (isClosed.get()) {
            throw new IllegalStateException("Channel is closed");
        }
        CommandPacket commandPacket = new CommandPacket(MySQLPacket.COM_QUERY, statement);
        out.write(commandPacket.toBytes());
        out.flush();
        return receive();
    }

    public BinaryPacket receive() throws IOException {
        if (isClosed.get()) {
            throw new IllegalStateException("Channel is closed");
        }
        byte[] header = new byte[4];
        in.read(header);
        int packetLength = ((header[0] & 0xFF) | ((header[1] & 0xFF) << 8) | ((header[2] & 0xFF) << 16));
        byte packetId = header[3];
        byte[] data = new byte[packetLength];
        in.read(data);
        return new BinaryPacket(packetId, data);
    }

    public void close() throws IOException {
        if (isClosed.get()) {
            return;
        }
        QuitPacket quitPacket = new QuitPacket();
        out.write(quitPacket.toBytes());
        out.flush();
        socket.close();
        isClosed.set(true);
    }

    public static class BinaryPacket {
        private final byte packetId;
        private final byte[] data;

        public BinaryPacket(byte packetId, byte[] data) {
            this.packetId = packetId;
            this.data = data;
        }

        public byte getPacketId() {
            return packetId;
        }

        public byte[] getData() {
            return data;
        }
    }

    public static class CommandPacket {
        private final byte commandType;
        private final String statement;

        public CommandPacket(byte commandType, String statement) {
            this.commandType = commandType;
            this.statement = statement;
        }

        public byte[] toBytes() {
            byte[] statementBytes = statement.getBytes();
            byte[] packet = new byte[1 + statementBytes.length];
            packet[0] = commandType;
            System.arraycopy(statementBytes, 0, packet, 1, statementBytes.length);
            return packet;
        }
    }

    public static class MySQLPacket {
        public static final byte COM_QUERY = 0x03;
    }

    public static class QuitPacket {
        public byte[] toBytes() {
            return new byte[]{MySQLPacket.COM_QUERY};
        }
    }
}

