import java.net.*;
import java.io.*;
import java.util.*;
import java.util.logging.*;

public class NetworkForwarder {
    private static final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private final InetAddress destination;
    private final DatagramSocket connectionUdp;
    private final Map<InetSocketAddress, Socket> connectionsTcp = new HashMap<>();

    public NetworkForwarder(Config config) throws UnknownHostException, SocketException {
        this.destination = InetAddress.getByName(config.getDestinationAddress());
        this.connectionUdp = new DatagramSocket();
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        if (datagram) {
            forwardUdp(port, data);
        } else {
            forwardTcp(source, port, data);
        }
    }

    private void forwardUdp(int port, byte[] data) {
        try {
            DatagramPacket packet = new DatagramPacket(data, data.length, destination, port);
            connectionUdp.send(packet);
        } catch (IOException e) {
            LOGGER.log(Level.WARNING, "Failed to forward UDP packet", e);
        }
    }

    private void forwardTcp(InetSocketAddress source, int port, byte[] data) {
        Socket socket = connectionsTcp.get(source);
        if (socket == null) {
            try {
                socket = new Socket(destination, port);
                connectionsTcp.put(source, socket);
            } catch (IOException e) {
                LOGGER.log(Level.WARNING, "Failed to establish TCP connection", e);
                return;
            }
        }
        try {
            OutputStream out = socket.getOutputStream();
            out.write(data);
            out.flush();
        } catch (IOException e) {
            LOGGER.log(Level.WARNING, "Failed to forward TCP data", e);
            disconnect(source);
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.remove(source);
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                LOGGER.log(Level.WARNING, "Failed to close TCP connection", e);
            }
        }
    }
}

