    private int port = 7800;
    private boolean server;
    private Logger log = Logger.getLogger(UdpTransport.class.getName());
    private List<SocketAddress> members = new CopyOnWriteArrayList<>();

    public interface RtReceiver {
        void receive(byte[] data, SocketAddress address);
    }

    public UdpTransport(boolean server, String host, int port) throws UnknownHostException {
        this.server = server;
        this.host = InetAddress.getByName(host);
        this.port = port;
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public Object localAddress() {
        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return Collections.unmodifiableList(members);
    }

    public void start(String... options) throws SocketException {
        sock = new DatagramSocket(port, host);
        receiverThread = new Receiver();
        receiverThread.start();
        log.info("UDP Transport started on " + host + ":" + port);
    }

    public void stop() {
        if (receiverThread != null) {
            receiverThread.interrupt();
        }
        if (sock != null && !sock.isClosed()) {
            sock.close();
        }
        log.info("UDP Transport stopped");
    }

    public void send(Object destination, byte[] data, int offset, int length) throws IOException {
        if (!(destination instanceof SocketAddress)) {
            throw new IllegalArgumentException("Destination must be a SocketAddress");
        }
        DatagramPacket packet = new DatagramPacket(data, offset, length, (SocketAddress) destination);
        sock.send(packet);
        log.info("Sent packet to " + destination);
    }

    private class Receiver extends Thread {
        public void run() {
            byte[] buffer = new byte[1024];
            while (!isInterrupted()) {
                try {
                    DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.receive(packet.getData(), packet.getSocketAddress());
                    }
                } catch (IOException e) {
                    if (!sock.isClosed()) {
                        log.warning("Error receiving packet: " + e.getMessage());
                    }
                }
            }
        }
    }
}

