        try {
            socket = new DatagramSocket();
            socket.setSoTimeout(timeout);
            byte[] buffer = new byte[NTP_PACKET_SIZE];
            writeNtpVersion(buffer);
            writeTimeStamp(buffer, 40, System.currentTimeMillis());
            DatagramPacket request = new DatagramPacket(buffer, buffer.length, address, NTP_PORT);
            socket.send(request);

            DatagramPacket response = new DatagramPacket(buffer, buffer.length);
            socket.receive(response);

            long originateTime = readTimeStamp(buffer, 24);
            long receiveTime = readTimeStamp(buffer, 32);
            long transmitTime = readTimeStamp(buffer, 40);
            long responseTime = System.currentTimeMillis();

            long roundTripDelay = (responseTime - originateTime) - (transmitTime - receiveTime);
            long clockOffset = ((receiveTime - originateTime) + (transmitTime - responseTime)) / 2;

            if (validateResponse(buffer, rootDelayMax, rootDispersionMax)) {
                return new SntpResult(clockOffset, roundTripDelay);
            } else {
                listener.onError("Invalid NTP response");
            }
        } catch (SocketTimeoutException e) {
            listener.onError("Request timed out");
        } catch (Exception e) {
            listener.onError("Error: " + e.getMessage());
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
        return null;
    }

    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) (NTP_MODE | (NTP_VERSION << 3));
    }

    private void writeTimeStamp(byte[] buffer, int offset, long time) {
        long seconds = time / 1000L + OFFSET_1900_TO_1970;
        long fraction = ((time % 1000L) * 0x100000000L) / 1000L;
        for (int i = 0; i < 4; i++) {
            buffer[offset + i] = (byte) (seconds >> (24 - (i * 8)));
        }
        for (int i = 0; i < 4; i++) {
            buffer[offset + 4 + i] = (byte) (fraction >> (24 - (i * 8)));
        }
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long seconds = read(buffer, offset);
        long fraction = read(buffer, offset + 4);
        return ((seconds - OFFSET_1900_TO_1970) * 1000L) + ((fraction * 1000L) / 0x100000000L);
    }

    private long read(byte[] buffer, int offset) {
        long value = 0;
        for (int i = 0; i < 4; i++) {
            value = (value << 8) | ui(buffer[offset + i]);
        }
        return value;
