        float rootDispersion = (ui(buffer[8]) * 256.0f + ui(buffer[9]) + ui(buffer[10]) / 256.0f + ui(buffer[11]) / 65536.0f);
        return rootDelay < rootDelayMax && rootDispersion < rootDispersionMax;
    }
