import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.nio.charset.Charset;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.LinkedBlockingQueue;

public class Statsd {
    private static final Charset CHARSET = Charset.forName("US-ASCII");
    private static Statsd instance;
    private final ExecutorService executor;
    private final LinkedBlockingQueue<String> queue;
    private final InetAddress address;
    private final int port;

    private Statsd(String host, int port) throws Exception {
        this.executor = Executors.newSingleThreadExecutor();
        this.queue = new LinkedBlockingQueue<>();
        this.address = InetAddress.getByName(host);
        this.port = port;
        startBackgroundThread();
    }

    public static synchronized Statsd getInstance(String host, int port) throws Exception {
        if (instance == null) {
            instance = new Statsd(host, port);
        }
        return instance;
    }

    private void startBackgroundThread() {
        executor.submit(() -> {
            try (DatagramSocket socket = new DatagramSocket()) {
                while (!Thread.currentThread().isInterrupted()) {
                    String metric = queue.take();
                    byte[] data = metric.getBytes(CHARSET);
                    DatagramPacket packet = new DatagramPacket(data, data.length, address, port);
                    socket.send(packet);
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });
    }

    public void sendMetric(String metric) {
        queue.offer(metric);
    }
}

