public class SingleClientUdpTracker {
    private static final long CONNECTION_EXPIRATION_TIME = 60000; // 1 minute
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = 3;

    private DatagramSocket serverSocket;
    private int interval;
    private int leechers;
    private int seeders;
    private boolean shutdown = false;
    private long currentConnection;
    private long connectedOn;

    public SingleClientUdpTracker(int interval, int leechers, int seeders) throws Exception {
        this.serverSocket = new DatagramSocket();
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
        this.currentConnection = generateConnectionId();
        this.connectedOn = System.currentTimeMillis();
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    public void run() {
        byte[] buffer = new byte[1024];
        while (!shutdown) {
            try {
                DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                serverSocket.receive(packet);
                byte[] response = processRequest(packet.getData());
                DatagramPacket responsePacket = new DatagramPacket(response, response.length, packet.getSocketAddress());
                serverSocket.send(responsePacket);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private byte[] processRequest(byte[] requestData) {
        ByteBuffer requestBuffer = ByteBuffer.wrap(requestData);
        int action = requestBuffer.getInt();
        int transactionId = requestBuffer.getInt();

        switch (action) {
            case CONNECT_CODE:
                return createConnectResponse(currentConnection, transactionId);
            case ANNOUNCE_CODE:
                if (System.currentTimeMillis() - connectedOn > CONNECTION_EXPIRATION_TIME) {
                    currentConnection = generateConnectionId();
                    connectedOn = System.currentTimeMillis();
                }
                return createAnnounceResponse(currentConnection, transactionId);
            default:
                return createError(transactionId, "Invalid action code");
        }
    }

    private long generateConnectionId() {
        return new Random().nextLong();
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(16);
        buffer.putInt(CONNECT_CODE);
        buffer.putInt(transactionId);
        buffer.putLong(connectionId);
        return buffer.array();
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(20);
        buffer.putInt(ANNOUNCE_CODE);
        buffer.putInt(transactionId);
        buffer.putInt(interval);
        buffer.putInt(leechers);
        buffer.putInt(seeders);
        return buffer.array();
    }

    private byte[] createError(int transactionId, String message) {
        byte[] messageBytes = message.getBytes();
        ByteBuffer buffer = ByteBuffer.allocate(8 + messageBytes.length);
        buffer.putInt(ERROR_CODE);
        buffer.putInt(transactionId);
        buffer.put(messageBytes);
        return buffer.array();
    }

    public void shutdown() {
        shutdown = true;
        serverSocket.close();
    }
}

