        }

        @Override
        public String toString() {
            return "Error{" +
                    "responseCode=" + responseCode +
                    ", output='" + output + '\'' +
                    '}';
        }
    }

    public interface LineRead {
        void readLine(String line);
    }

    public static String post(String request) throws Exception {
        return post(request, "");
    }

    public static String post(String request, String body) throws Exception {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(request);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setRequestProperty("Content-Type", "application/json");

            try (OutputStream os = connection.getOutputStream()) {
                os.write(body.getBytes());
                os.flush();
            }

            return handleResponse(connection, null);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    public static String get(String request) throws Exception {
        return get(request, null, 5000);
    }

    public static String get(String request, LineRead callback, int timeout) throws Exception {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(request);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("GET");
            connection.setConnectTimeout(timeout);
            connection.setReadTimeout(timeout);

            return handleResponse(connection, callback);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private static String handleResponse(HttpURLConnection connection, LineRead callback) throws Exception {
        int responseCode = connection.getResponseCode();
        BufferedReader reader;
        if (responseCode >= 200 && responseCode < 300) {
            reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        } else {
            reader = new BufferedReader(new InputStreamReader(connection.getErrorStream()));
        }

        StringBuilder response = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            if (callback != null) {
                callback.readLine(line);
            }
            response.append(line);
        }
        reader.close();

        if (responseCode >= 200 && responseCode < 300) {
            return response.toString();
        } else {
            throw new Exception(new Error(responseCode, response.toString()).toString());
        }
    }
}

