import java.net.*;
import java.nio.ByteBuffer;
import java.util.Date;
import java.util.List;
import java.util.Random;
import java.util.logging.Logger;

public class UDPTrackerClient {
    private static final int UDP_BASE_TIMEOUT_SECONDS = 15;
    private static final int UDP_MAX_TRIES = 3;
    private static final int UDP_MAX_TRIES_ON_STOPPED = 1;
    private static final int UDP_PACKET_LENGTH = 512;

    private final Logger logger;
    private final InetSocketAddress address;
    private final Random random;
    private DatagramSocket socket;
    private Date connectionExpiration;
    private long connectionId;
    private int transactionId;
    private boolean stop;

    public UDPTrackerClient(List<Peer> peers, URI tracker) throws SocketException {
        this.logger = Logger.getLogger(UDPTrackerClient.class.getName());
        this.address = new InetSocketAddress(tracker.getHost(), tracker.getPort());
        this.random = new Random();
        this.socket = new DatagramSocket();
        this.stop = false;
    }

    public void announce(AnnounceRequestMessage.RequestEvent event, boolean inhibitEvents, AnnounceableInformation torrent, List<Peer> peers) {
        try {
            if (connectionId == 0 || new Date().after(connectionExpiration)) {
                connect();
            }
            UDPAnnounceRequestMessage request = buildAnnounceRequest(event, torrent, peers.get(0));
            send(request.getData());
            ByteBuffer response = recv(UDP_MAX_TRIES);
            TrackerMessage message = TrackerMessage.parse(response);
            validateTrackerResponse(message);
            handleTrackerAnnounceResponse(message, inhibitEvents, torrent.getHexInfoHash());
        } catch (Exception e) {
            logger.severe("Announce failed: " + e.getMessage());
        }
    }

    private void connect() throws Exception {
        transactionId = random.nextInt();
        ByteBuffer connectRequest = ByteBuffer.allocate(16);
        connectRequest.putLong(0x41727101980L); // Connection ID
        connectRequest.putInt(0); // Action (connect)
        connectRequest.putInt(transactionId);
        send(connectRequest);
        ByteBuffer response = recv(UDP_MAX_TRIES);
        TrackerMessage message = TrackerMessage.parse(response);
        validateTrackerResponse(message);
        handleTrackerConnectResponse(message);
    }

    private void handleTrackerConnectResponse(TrackerMessage message) {
        if (message instanceof ConnectResponse) {
            ConnectResponse response = (ConnectResponse) message;
            connectionId = response.getConnectionId();
            connectionExpiration = new Date(System.currentTimeMillis() + UDP_BASE_TIMEOUT_SECONDS * 1000);
        }
    }

    private void handleTrackerAnnounceResponse(TrackerMessage message, boolean inhibitEvents, String hexInfoHash) {
        if (message instanceof AnnounceResponse) {
            AnnounceResponse response = (AnnounceResponse) message;
            logger.info("Announce response received: " + response.getPeers().size() + " peers");
            if (!inhibitEvents) {
                // Handle peers
            }
        }
    }

    private UDPAnnounceRequestMessage buildAnnounceRequest(AnnounceRequestMessage.RequestEvent event, AnnounceableInformation torrent, Peer peer) {
        return new UDPAnnounceRequestMessage(connectionId, transactionId, event, torrent, peer);
    }

    private void validateTrackerResponse(TrackerMessage message) throws Exception {
        if (message.getTransactionId() != transactionId) {
            throw new Exception("Invalid transaction ID in response");
        }
    }

    private void send(ByteBuffer data) throws Exception {
        DatagramPacket packet = new DatagramPacket(data.array(), data.limit(), address);
        socket.send(packet);
    }

    private ByteBuffer recv(int attempt) throws Exception {
        socket.setSoTimeout(UDP_BASE_TIMEOUT_SECONDS * 1000);
        DatagramPacket packet = new DatagramPacket(new byte[UDP_PACKET_LENGTH], UDP_PACKET_LENGTH);
        for (int i = 0; i < attempt; i++) {
            try {
                socket.receive(packet);
                return ByteBuffer.wrap(packet.getData(), 0, packet.getLength());
            } catch (SocketTimeoutException e) {
                logger.warning("Timeout, retrying... (" + (i + 1) + "/" + attempt + ")");
            }
        }
        throw new Exception("Failed to receive response after " + attempt + " attempts");
    }

    public void close() {
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }
}

