import java.net.*;
import java.util.ArrayList;
import java.util.List;

public class DnsUdpClient {
    private static final int DNS_PORT = 53;
    private static final int BUFFER_SIZE = 512;
    private static final int TIMEOUT = 5000;

    private InetSocketAddress[] DNS_SERVERS;

    public DnsUdpClient(InetSocketAddress[] dnsServers) {
        this.DNS_SERVERS = dnsServers;
    }

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        byte[] response = sendUdpQuery(dnsServer, query);
        return toIps(response);
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        byte[] response = sendUdpQuery(dnsServer, query);
        return toTXT(response);
    }

    private byte[] sendUdpQuery(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.setSoTimeout(TIMEOUT);
        DatagramPacket request = new DatagramPacket(query, query.length, dnsServer.getAddress(), DNS_PORT);
        socket.send(request);

        byte[] buffer = new byte[BUFFER_SIZE];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);
        socket.close();

        byte[] responseData = new byte[response.getLength()];
        System.arraycopy(response.getData(), 0, responseData, 0, response.getLength());
        return responseData;
    }

    private List<Inet4Address> toIps(byte[] buffer) throws Exception {
        List<Inet4Address> ips = new ArrayList<>();
        int answerCount = ((buffer[6] & 0xFF) << 8) | (buffer[7] & 0xFF);
        int pos = 12;

        while (buffer[pos] != 0) {
            pos += (buffer[pos] & 0xFF) + 1;
        }
        pos += 5;

        for (int i = 0; i < answerCount; i++) {
            pos += 10;
            int dataLength = ((buffer[pos] & 0xFF) << 8) | (buffer[pos + 1] & 0xFF);
            pos += 2;
            if (dataLength == 4) {
                byte[] ipBytes = new byte[4];
                System.arraycopy(buffer, pos, ipBytes, 0, 4);
                ips.add((Inet4Address) InetAddress.getByAddress(ipBytes));
            }
            pos += dataLength;
        }
        return ips;
    }

    private String toTXT(byte[] buffer) {
        int answerCount = ((buffer[6] & 0xFF) << 8) | (buffer[7] & 0xFF);
        int pos = 12;

        while (buffer[pos] != 0) {
            pos += (buffer[pos] & 0xFF) + 1;
        }
        pos += 5;

        for (int i = 0; i < answerCount; i++) {
            pos += 10;
            int dataLength = ((buffer[pos] & 0xFF) << 8) | (buffer[pos + 1] & 0xFF);
            pos += 2;
            if (buffer[pos - 10] == 0x10) {
                int txtLength = buffer[pos] & 0xFF;
                return new String(buffer, pos + 1, txtLength);
            }
            pos += dataLength;
        }
        throw new RuntimeException("No TXT record found");
    }
}

