            logger.log(Level.SEVERE, HTTP_ERROR_MESSAGE, e);
            throw new IOException(HTTP_ERROR_MESSAGE, e);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    public static String mapToQueryString(Map<String, String> parameters, String charSet) throws UnsupportedEncodingException {
        StringBuilder queryString = new StringBuilder();
        for (Map.Entry<String, String> entry : parameters.entrySet()) {
            if (queryString.length() > 0) {
                queryString.append("&");
            }
            queryString.append(URLEncoder.encode(entry.getKey(), charSet)).append("=").append(URLEncoder.encode(entry.getValue(), charSet));
