        Properties config = new Properties();
        config.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, kafkaClientInfo.getBootstrapServers());
        config.put(AdminClientConfig.CLIENT_ID_CONFIG, kafkaClientInfo.getClientId());

        try (AdminClient adminClient = AdminClient.create(config)) {
            NewTopic newTopic = new NewTopic(newTopicInfo.getName(), newTopicInfo.getNumPartitions(), newTopicInfo.getReplicationFactor());
            newTopic.configs(Collections.singletonMap("retention.ms", String.valueOf(newTopicInfo.getRetentionTimeMs())));

            KafkaFuture<Void> future = adminClient.createTopics(Collections.singleton(newTopic)).all();
            future.get();
            return true;
        } catch (ExecutionException e) {
            if (e.getCause() instanceof TopicExistsException) {
                System.out.println("Topic already exists.");
            } else {
                e.printStackTrace();
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            e.printStackTrace();
        }
        return false;
    }
