import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public final class HttpURLConnectionUtils {

    private static final int TIMEOUT_IN_MILLIS = 5000;
    private static final ExecutorService executor = Executors.newCachedThreadPool();

    private HttpURLConnectionUtils() {
    }

    public static void doGetAsync(String urlStr, Callback callback) {
        request("GET", urlStr, null, null, callback);
    }

    public static void doPostAsync(String urlStr, String params, Callback callback) {
        request("POST", urlStr, null, params, callback);
    }

    private static void request(String method, String urlStr, Map<String, String> headers, String params, Callback callback) {
        executor.submit(() -> {
            HttpURLConnection connection = null;
            BufferedReader reader = null;
            try {
                URL url = new URL(urlStr);
                connection = (HttpURLConnection) url.openConnection();
                connection.setRequestMethod(method);
                connection.setConnectTimeout(TIMEOUT_IN_MILLIS);
                connection.setReadTimeout(TIMEOUT_IN_MILLIS);

                if (headers != null) {
                    for (Map.Entry<String, String> entry : headers.entrySet()) {
                        connection.setRequestProperty(entry.getKey(), entry.getValue());
                    }
                }

                if ("POST".equalsIgnoreCase(method) && params != null) {
                    connection.setDoOutput(true);
                    try (OutputStreamWriter writer = new OutputStreamWriter(connection.getOutputStream())) {
                        writer.write(params);
                    }
                }

                int responseCode = connection.getResponseCode();
                InputStreamReader streamReader = responseCode >= 200 && responseCode < 300 ?
                    new InputStreamReader(connection.getInputStream()) :
                    new InputStreamReader(connection.getErrorStream());

                reader = new BufferedReader(streamReader);
                StringBuilder result = new StringBuilder();
                String line;
                while ((line = reader.readLine()) != null) {
                    result.append(line);
                }

                callback.onResponse(result.toString(), responseCode);
            } catch (Throwable error) {
                callback.onFail(error);
            } finally {
                if (reader != null) {
                    try {
                        reader.close();
                    } catch (IOException ignore) {
                    }
                }
                if (connection != null) {
                    connection.disconnect();
                }
            }
        });
    }

    public interface Callback {
        void onResponse(String result, long responseCode);

        void onFail(Throwable error);
    }
}

