    public HttpUrlSource(String url, HostnameVerifier hostnameVerifier, TrustManager[] trustAllCerts) {
        this(url, null, null, hostnameVerifier, trustAllCerts);
    }

    public HttpUrlSource(String url, SourceInfoStorage sourceInfoStorage, HostnameVerifier hostnameVerifier, TrustManager[] trustAllCerts) {
        this(url, sourceInfoStorage, null, hostnameVerifier, trustAllCerts);
    }

    public HttpUrlSource(String url, SourceInfoStorage sourceInfoStorage, HeaderInjector headerInjector, HostnameVerifier hostnameVerifier, TrustManager[] trustAllCerts) {
        this.sourceInfoStorage = sourceInfoStorage;
        this.headerInjector = headerInjector;
        this.hostnameVerifier = hostnameVerifier;
        this.trustAllCerts = trustAllCerts;
        this.sourceInfo = new SourceInfo(url);
    }

    public long length() {
        if (sourceInfo.getLength() == -1) {
            fetchContentInfo();
        }
        return sourceInfo.getLength();
    }

    public void open(long offset) throws Exception {
        close();
        connection = openConnection(offset, 30000);
        inputStream = connection.getInputStream();
    }

    public void close() {
        if (inputStream != null) {
            try {
                inputStream.close();
            } catch (Exception ignored) {
            }
        }
        if (connection != null) {
            connection.disconnect();
        }
    }

    public int read(byte[] buffer) throws Exception {
        return inputStream.read(buffer);
    }

    public String getMime() {
        if (sourceInfo.getMimeType() == null) {
            fetchContentInfo();
        }
        return sourceInfo.getMimeType();
    }

    public String getUrl() {
        return sourceInfo.getUrl();
    }

    private HttpURLConnection openConnection(long offset, int timeout) throws Exception {
        URL url = new URL(sourceInfo.getUrl());
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setConnectTimeout(timeout);
        connection.setReadTimeout(timeout);
        connection.setRequestProperty("Range", "bytes=" + offset + "-");
        if (hostnameVerifier != null && connection instanceof HttpsURLConnection) {
            ((HttpsURLConnection) connection).setHostnameVerifier(hostnameVerifier);
        }
        if (trustAllCerts != null && connection instanceof HttpsURLConnection) {
            SSLContext sc = SSLContext.getInstance("TLS");
            sc.init(null, trustAllCerts, new java.security.SecureRandom());
            ((HttpsURLConnection) connection).setSSLSocketFactory(sc.getSocketFactory());
        }
        injectCustomHeaders(connection, sourceInfo.getUrl());
        return connection;
    }

    private void injectCustomHeaders(HttpURLConnection connection, String url) {
        if (headerInjector != null) {
            Map<String, String> headers = headerInjector.getHeaders(url);
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                connection.setRequestProperty(entry.getKey(), entry.getValue());
            }
        }
    }

    private long readSourceAvailableBytes(HttpURLConnection connection, long offset, int timeout) throws Exception {
        long contentLength = getContentLength(connection);
        return contentLength - offset;
    }

    private long getContentLength(HttpURLConnection connection) {
        String contentLengthStr = connection.getHeaderField("Content-Length");
        return contentLengthStr != null ? Long.parseLong(contentLengthStr) : -1;
    }

    private void fetchContentInfo() {
        try {
            HttpURLConnection connection = openConnection(0, 30000);
            sourceInfo.setLength(getContentLength(connection));
            sourceInfo.setMimeType(connection.getContentType());
            if (sourceInfoStorage != null) {
                sourceInfoStorage.store(sourceInfo);
            }
            connection.disconnect();
        } catch (Exception ignored) {
        }
    }
}

class SourceInfo {
    private final String url;
    private long length = -1;
    private String mimeType;

    public SourceInfo(String url) {
        this.url = url;
    }

    public String getUrl() {
