import com.fasterxml.jackson.databind.ObjectMapper;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.charset.StandardCharsets;

public class JobRemotingUtil {

    private static final Logger logger = LoggerFactory.getLogger(JobRemotingUtil.class);
    private static final String XXL_RPC_ACCESS_TOKEN = "XXL-RPC-ACCESS-TOKEN";
    private static final ObjectMapper objectMapper = new ObjectMapper();

    public static ReturnT<String> postBody(String url, String accessToken, Object requestObj, int timeout) {
        HttpURLConnection connection = null;
        try {
            URL requestUrl = new URL(url);
            connection = (HttpURLConnection) requestUrl.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setRequestProperty("Content-Type", "application/json");
            connection.setRequestProperty(XXL_RPC_ACCESS_TOKEN, accessToken);
            connection.setConnectTimeout(timeout * 1000);
            connection.setReadTimeout(timeout * 1000);

            String jsonPayload = objectMapper.writeValueAsString(requestObj);
            try (OutputStream os = connection.getOutputStream()) {
                os.write(jsonPayload.getBytes(StandardCharsets.UTF_8));
            }

            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                String responseContent = new String(connection.getInputStream().readAllBytes(), StandardCharsets.UTF_8);
                return new ReturnT<>(ReturnT.SUCCESS_CODE, "Success", responseContent);
            } else {
                String errorContent = new String(connection.getErrorStream().readAllBytes(), StandardCharsets.UTF_8);
                logger.error("HTTP request failed with code: {} and message: {}", responseCode, errorContent);
                return new ReturnT<>(ReturnT.FAIL_CODE, "Failed", errorContent);
            }
        } catch (Exception e) {
            logger.error("Exception occurred during HTTP request", e);
            return new ReturnT<>(ReturnT.FAIL_CODE, "Exception", e.getMessage());
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
}

