import android.app.DownloadManager;
import android.content.Context;
import android.net.Uri;
import android.webkit.MimeTypeMap;
import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;
import io.reactivex.Single;

public class FetchUrlMimeType {

    private DownloadManager.Request mRequest;
    private DownloadManager mDownloadManager;
    private String mUri;
    private String mCookies;
    private String mUserAgent;

    public FetchUrlMimeType(DownloadManager downloadManager, DownloadManager.Request request, String uri, String cookies, String userAgent) {
        this.mDownloadManager = downloadManager;
        this.mRequest = request;
        this.mUri = uri;
        this.mCookies = cookies;
        this.mUserAgent = userAgent;
    }

    public Single<Result> create() {
        return Single.create(emitter -> {
            HttpURLConnection connection = null;
            try {
                URL url = new URL(mUri);
                connection = (HttpURLConnection) url.openConnection();
                if (mCookies != null) {
                    connection.setRequestProperty("Cookie", mCookies);
                }
                if (mUserAgent != null) {
                    connection.setRequestProperty("User-Agent", mUserAgent);
                }
                connection.connect();

                if (connection.getResponseCode() != HttpURLConnection.HTTP_OK) {
                    emitter.onSuccess(Result.FAILURE_ENQUEUE);
                    return;
                }

                String mimeType = connection.getContentType();
                String contentDisposition = connection.getHeaderField("Content-Disposition");
                String fileName = getFileName(contentDisposition, mimeType);

                mRequest.setMimeType(mimeType);
                mRequest.setTitle(fileName);
                mRequest.setDestinationInExternalPublicDir("/Download", fileName);

                long downloadId = mDownloadManager.enqueue(mRequest);
                if (downloadId == -1) {
                    emitter.onSuccess(Result.FAILURE_ENQUEUE);
                } else {
                    emitter.onSuccess(Result.SUCCESS);
                }
            } catch (IOException e) {
                emitter.onSuccess(Result.FAILURE_LOCATION);
            } finally {
                if (connection != null) {
                    connection.disconnect();
                }
            }
        });
    }

    private String getFileName(String contentDisposition, String mimeType) {
        String fileName = "downloadfile";
        if (contentDisposition != null && contentDisposition.contains("filename=")) {
            fileName = contentDisposition.substring(contentDisposition.indexOf("filename=") + 9);
            fileName = fileName.replaceAll("\"", "");
        } else {
            String extension = MimeTypeMap.getSingleton().getExtensionFromMimeType(mimeType);
            fileName = "downloadfile." + (extension != null ? extension : "bin");
        }
        return fileName;
    }

    public enum Result {
        FAILURE_ENQUEUE,
        FAILURE_LOCATION,
        SUCCESS
    }
}
