import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.charset.StandardCharsets;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.function.Supplier;

import jdk.incubator.foreign.Arena;
import jdk.incubator.foreign.MemorySegment;

interface ReaderSupplier extends Supplier<MemorySegmentReader>, AutoCloseable { }

class MemorySegmentReader {
    private final MemorySegment segment;

    MemorySegmentReader(MemorySegment segment) {
        this.segment = segment;
    }

    public String readString() {
        ByteBuffer buffer = segment.asByteBuffer().duplicate();
        return StandardCharsets.UTF_8.decode(buffer).toString();
    }

    public byte[] readBytes() {
        ByteBuffer buffer = segment.asByteBuffer().duplicate();
        byte[] bytes = new byte[buffer.remaining()];
        buffer.get(bytes);
        return bytes;
    }
}

public class MemorySegmentReaderSupplier implements ReaderSupplier {

    private final Arena arena;
    private final MemorySegment memory;

    public MemorySegmentReaderSupplier(Path path) throws IOException {
        arena = Arena.ofConfined();
        FileChannel channel = FileChannel.open(path, StandardOpenOption.READ);
        memory = channel.map(FileChannel.MapMode.READ_ONLY, 0, channel.size(), arena);
        channel.close();
    }

    @Override
    public MemorySegmentReader get() {
        return new MemorySegmentReader(memory);
    }

    @Override
    public void close() {
        arena.close();
    }
}

