import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.function.Consumer;

import org.openstreetmap.osmosis.osmbinary.Osmformat;
import org.openstreetmap.osmosis.osmbinary.file.BlockInputStream;

public class OsmInputFile {

    private final Path path;
    private final boolean lazy;

    public OsmInputFile(Path path, boolean lazy) {
        this.path = path;
        this.lazy = lazy;
    }

    private int readInt(FileChannel channel) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        channel.read(buffer);
        buffer.flip();
        return buffer.getInt();
    }

    private byte[] readBytes(FileChannel channel, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer);
        buffer.flip();
        return buffer.array();
    }

    private byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer, offset);
        buffer.flip();
        return buffer.array();
    }


    private Osmformat.BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        int headerSize = readInt(channel);
        byte[] headerData = readBytes(channel, headerSize);
        return Osmformat.BlobHeader.parseFrom(headerData);
    }

    private FileChannel openChannel() throws IOException {
        return FileChannel.open(path, StandardOpenOption.READ);
    }

    public class LazyReader implements OsmBlockSource {
        private final FileChannel channel;

        public LazyReader() throws IOException {
            this.channel = openChannel();
        }

        public void forEachBlock(Consumer<Block> consumer) throws IOException {
            while (channel.position() < channel.size()) {
                Osmformat.BlobHeader blobHeader = readBlobHeader(channel);
                byte[] blobData = readBytes(channel, blobHeader.getDatasize());
                consumer.accept(new LazyBlock(channel.position() - blobHeader.getDatasize() - 4, blobHeader.getDatasize(), channel));
            }
        }


        public void close() throws IOException {
            channel.close();
        }

        private record LazyBlock(long offset, int length, FileChannel channel) implements Block {
            public Iterable<OsmElement> decodeElements() throws IOException {
                byte[] blobData = readBytes(channel, offset, length);
                Osmformat.Blob blob = Osmformat.Blob.parseFrom(blobData);
                return BlockInputStream.decodeElements(blob);
            }
        }
    }


    public interface OsmBlockSource {
        void forEachBlock(Consumer<Block> consumer) throws IOException;
        void close() throws IOException;
    }

    public interface Block {
        Iterable<OsmElement> decodeElements() throws IOException;
    }

    public interface OsmElement {}


}

