        this.unprocessedRequests = new UnprocessedRequests();
        this.channelProvider = new ChannelProvider();
        this.eventLoopGroup = new NioEventLoopGroup();
        this.bootstrap = new Bootstrap();
        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new LengthFieldBasedFrameDecoder(Integer.MAX_VALUE, 0, 4, 0, 4));
                        pipeline.addLast(new LengthFieldPrepender(4));
                        pipeline.addLast(new ObjectEncoder());
                        pipeline.addLast(new ObjectDecoder(ClassResolvers.cacheDisabled(null)));
                        pipeline.addLast(new NettyClientHandler(unprocessedRequests));
                    }
                });
    }

    private Channel doConnect(InetSocketAddress inetSocketAddress) throws InterruptedException {
        ChannelFuture future = bootstrap.connect(inetSocketAddress);
        future.sync();
        return future.channel();

    }


    @Override
    public Object sendRpcRequest(RpcRequest rpcRequest) {
        InetSocketAddress inetSocketAddress = serviceDiscovery.discoverService(rpcRequest.getInterfaceName());
        Channel channel = getChannel(inetSocketAddress);

        CompletableFuture<Object> resultFuture = new CompletableFuture<>();
        unprocessedRequests.put(rpcRequest.getRequestId(), resultFuture);

        channel.writeAndFlush(rpcRequest).addListener((ChannelFutureListener) future -> {
            if (!future.isSuccess()) {
                resultFuture.completeExceptionally(future.cause());
                channel.close();
            }
        });
        return resultFuture;
    }


    private Channel getChannel(InetSocketAddress inetSocketAddress) {
        Channel channel = channelProvider.get(inetSocketAddress);
        if (channel == null || !channel.isActive()) {
            try {
                channel = doConnect(inetSocketAddress);
                channelProvider.put(inetSocketAddress, channel);
            } catch (InterruptedException e) {
                throw new RuntimeException("Connection Interrupted", e);
            }
        }
        return channel;
    }


    @Override
    public void close() {
        eventLoopGroup.shutdownGracefully();
        channelProvider.closeAll();
    }
