                        ChannelPipeline pipeline = ch.pipeline();
                        if (sslContext != null) {
                            pipeline.addLast(sslContext.newHandler(ch.alloc(), host, port));
                        }
                        pipeline.addLast(
                                new HttpClientCodec(),
                                new HttpObjectAggregator(8192),
                                new IdleStateHandler(
                                        options.getAutoPingInterval() + options.getAutoPingTimeout(),
                                        options.getAutoPingInterval(), 0, TimeUnit.SECONDS),
                                mHandler);
                    }
                });

        ChannelFuture f = bootstrap.connect(uri.getHost(), port);
        f.addListener((ChannelFutureListener) connectFuture -> {
            if (connectFuture.cause() != null) {
                transportHandler.onDisconnect(false);
            } else {
                mChannel = f.channel();
            }
        });
    }

    @Override
    public void send(byte[] payload, boolean isBinary) {
        WebSocketFrame frame = isBinary ?
                new BinaryWebSocketFrame(toByteBuf(payload)) :
                new TextWebSocketFrame(toByteBuf(payload));
        mChannel.writeAndFlush(frame);
    }

