    @Override
    public void close() throws IOException {
        this.channel.close();
    }

    @Override
    public CloseableIterator<Tile> getAllTiles() {
        try {
            List<Pmtiles.Entry> dir = readDir(header.getRootOffset(), header.getRootLength());
            return new CloseableIteratorImpl(getTiles(dir));
        } catch (IOException e) {
