    }

    private void connectProxyServer() {
        bootstrap.connect(config.getProxyHost(), config.getProxyPort()).addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                logger.info("Successfully connected to proxy server");
                sleepTimeMill = 1000; // Reset sleep time on successful connection
            } else {
                logger.error("Failed to connect to proxy server", future.cause());
                reconnectWait();
            }
        });
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }


