    private void reconnectWait() {
        try {
            TimeUnit.MILLISECONDS.sleep(sleepTimeMill);
            connectProxyServer();
            sleepTimeMill *= 2; // Exponential backoff
            if (sleepTimeMill > config.getMaxReconnectDelay()) {
                sleepTimeMill = config.getMaxReconnectDelay();
            }
        } catch (InterruptedException e) {
            logger.error("Interrupted during reconnection wait", e);
            Thread.currentThread().interrupt();
        }
