import java.io.*;
import java.nio.*;
import java.nio.channels.*;
import java.nio.file.*;
import java.util.logging.*;

abstract class AppendStoreMmap implements Closeable {

    protected final DataOutputStream outputStream;
    protected final int segmentBits;
    protected final long segmentMask;
    protected final long segmentBytes;
    protected final Path path;
    protected final boolean madvise;
    protected long outIdx = 0;
    protected MappedByteBuffer[] segments = null;
    protected FileChannel channel;
    private static final Logger LOGGER = Logger.getLogger(AppendStoreMmap.class.getName());

    public AppendStoreMmap(Path path, boolean madvise) throws IOException {
        this(path, 30, madvise);
    }

    public AppendStoreMmap(Path path, long segmentSizeBytes, boolean madvise) throws IOException {
        this.path = path;
        this.madvise = madvise;
        this.segmentBits = (int) (Math.log(segmentSizeBytes) / Math.log(2));
        this.segmentBytes = 1L << segmentBits;
        this.segmentMask = segmentBytes - 1;
        this.channel = FileChannel.open(path, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
        this.outputStream = new DataOutputStream(Channels.newOutputStream(channel));
    }


    public synchronized MappedByteBuffer[] getSegments() throws IOException {
        if (segments == null) {
            long fileSize = channel.size();
            int numSegments = (int) ((fileSize + segmentBytes - 1) >> segmentBits);
            segments = new MappedByteBuffer[numSegments];
            for (int i = 0; i < numSegments; i++) {
                long position = (long) i << segmentBits;
                long size = Math.min(segmentBytes, fileSize - position);
                segments[i] = channel.map(FileChannel.MapMode.READ_WRITE, position, size);
                if (madvise) {
                    try {
                        Class<?> madviseClass = Class.forName("sun.nio.ch.DirectBuffer");
                        java.lang.reflect.Method madviseMethod = madviseClass.getMethod("madvise", int.class);
                        madviseMethod.invoke(segments[i], 1 /* POSIX_MADV_RANDOM */);
                    } catch (Exception e) {
                        LOGGER.warning("Failed to set madvise: " + e.getMessage());
                    }
                }
            }
        }
        return segments;
    }

    @Override
    public synchronized void close() throws IOException {
        if (segments != null) {
            for (MappedByteBuffer segment : segments) {
                if (segment != null) {
                    try {
                        Class<?> cleanerClass = Class.forName("sun.misc.Cleaner");
                        java.lang.reflect.Method cleanMethod = cleanerClass.getMethod("clean");
                        cleanMethod.invoke(segment);
                    } catch (Exception e) {
                        LOGGER.warning("Failed to unmap segment: " + e.getMessage());
                    }
                }
            }
            segments = null;
        }
        if (outputStream != null) {
            outputStream.close();
        }
        if (channel != null) {
            channel.close();
        }
    }

    public long diskUsageBytes() throws IOException {
        return channel.size();
    }
