                SpectatorUtils.buildAndRegisterCounter(
                        registry, "connectionSuccess", "channel", HttpEventChannel.CHANNEL_TYPE);
        this.connectionFailure =
                SpectatorUtils.buildAndRegisterCounter(
                        registry, "connectionFailure", "channel", HttpEventChannel.CHANNEL_TYPE);
