import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.locks.ReadWriteLock;
import java.util.concurrent.locks.ReentrantReadWriteLock;

import org.apache.bookkeeper.common.util.nativeio.NativeIO;

public class CachedOpenFile implements AutoCloseable {

    private FileChannel fc;
    private final ReadWriteLock lock = new ReentrantReadWriteLock();
    private final long capacity;

    public CachedOpenFile(Path file, long capacity) throws IOException {
        Files.createDirectories(file.getParent());
        this.fc = FileChannel.open(file, StandardOpenOption.CREATE,
                StandardOpenOption.READ, StandardOpenOption.WRITE);
        this.capacity = capacity;
    }

    public int readBlock(ByteBuffer buffer, long offset) throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            return fc.read(buffer, offset);
        } finally {
            unlockForIoOperation();
        }
    }

    public int writeBlock(ByteBuffer buffer, long offset) throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            return fc.write(buffer, offset);
        } finally {
            unlockForIoOperation();
        }
    }

    public int writeBlock(ByteBufferView buffer, long offset) throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            return NativeIO.write(fc, buffer.getNioBuffer(), offset, buffer.isDirect());
        } finally {
            unlockForIoOperation();
        }
    }


    public long capacity() {
        return capacity;
    }

    public long size() throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            return fc.size();
        } finally {
            unlockForIoOperation();
        }
    }

    public void flush() throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            fc.force(true);
        } finally {
            unlockForIoOperation();
        }
    }

    private void lockForIoOperation() {
        lock.readLock().lock();
    }

    private void unlockForIoOperation() {
        lock.readLock().unlock();
    }

    @Override
    public void close() throws IOException {
        lock.writeLock().lock();
        try {
            if (fc != null) {
                fc.close();
                fc = null;
            }
        } finally {
            lock.writeLock().unlock();
        }
    }

    private void ensureOpen() throws IOException {
        if (fc == null) {
            throw new IOException("File closed");
        }
    }

    // Dummy ByteBufferView class for compilation
    private static class ByteBufferView {
        public ByteBuffer getNioBuffer() { return ByteBuffer.allocate(0); }
        public boolean isDirect() { return false; }
    }

}

