    }

    private <T extends Channel> T configure(T channel) throws Exception {
        ChannelPipeline pipeline = channel.pipeline();
        pipeline.addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
        return channel;
    }


    private class ReadTimeoutHandler extends io.netty.handler.timeout.ReadTimeoutHandler {
        public ReadTimeoutHandler(long timeoutMillis) {
            super(timeoutMillis);
        }

        public ReadTimeoutHandler(long timeout, TimeUnit unit) {
            super(timeout, unit);
        }

        @Override
        protected void readTimedOut(ChannelHandlerContext ctx) throws Exception {
            ctx.fireExceptionCaught(new ReadTimeoutException("Read timed out after " + readTimeout + "ms"));
            ctx.close();
        }
    }

    private static class NamedThreadFactory implements java.util.concurrent.ThreadFactory {
        private final String prefix;

        public NamedThreadFactory(String prefix) {
            this.prefix = prefix;
        }

        @Override
        public Thread newThread(Runnable r) {
            Thread t = new Thread(r);
            t.setName(prefix + "-" + t.getId());
            return t;
