import java.io.Closeable;
import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.function.LongConsumer;
import java.util.function.LongSupplier;

import org.apache.lucene.store.AlreadyClosedException;
import org.apache.lucene.util.IOUtils;
import org.elasticsearch.common.lease.ReleasableLock;
import org.elasticsearch.common.unit.ByteSizeValue;
import org.elasticsearch.common.util.BigArrays;
import org.elasticsearch.common.util.io.ReleasableBytesStreamOutput;
import org.elasticsearch.index.shard.ShardId;
import org.elasticsearch.index.translog.BaseTranslogReader;
import org.elasticsearch.index.translog.Checkpoint;
import org.elasticsearch.index.translog.TranslogException;
import org.elasticsearch.index.translog.TranslogHeader;
import org.elasticsearch.index.translog.TragicExceptionHolder;

public class TranslogWriter extends BaseTranslogReader implements Closeable {
    private final FileChannel channel;
    private final FileChannel checkpointChannel;
    private final Path checkpointPath;
    private final BigArrays bigArrays;
    private long totalOffset;
    private final AtomicBoolean closed = new AtomicBoolean(false);
    private final ReleasableLock writeLock;
    private final Object syncLock = new Object();
    private ReleasableBytesStreamOutput buffer;
    private final LongSupplier globalCheckpointSupplier;
    private final LongSupplier minTranslogGenerationSupplier;
    private final LongConsumer persistedSequenceNumberConsumer;


    public TranslogWriter(ShardId shardId, Checkpoint checkpoint, FileChannel channel, FileChannel checkpointChannel, Path path,
                          Path checkpointPath, ByteSizeValue bufferSize, LongSupplier globalCheckpointSupplier,
                          LongSupplier minTranslogGenerationSupplier, TranslogHeader header, TragicExceptionHolder tragedy,
                          LongConsumer persistedSequenceNumberConsumer, BigArrays bigArrays) {
        super(shardId, checkpoint, path, header, tragedy);
        this.channel = channel;
        this.checkpointChannel = checkpointChannel;
        this.checkpointPath = checkpointPath;
        this.bigArrays = bigArrays;
        this.globalCheckpointSupplier = globalCheckpointSupplier;
        this.minTranslogGenerationSupplier = minTranslogGenerationSupplier;
        this.persistedSequenceNumberConsumer = persistedSequenceNumberConsumer;
        this.writeLock = new ReleasableLock(syncLock);
        this.totalOffset = checkpoint.offset();
        this.buffer = new ReleasableBytesStreamOutput(bufferSize, bigArrays);
    }

    public void writeAndReleaseOps(ReleasableBytesReference bytes) throws IOException {
        try (ReleasableLock lock = writeLock.acquire()) {
            if (closed.get()) {
                throw new AlreadyClosedException("translog is already closed");
            }
            buffer.writeBytes(bytes.bytes(), bytes.offset(), bytes.length());
            if (buffer.size() >= buffer.bytes().length / 4) {
                writeToFile(buffer.bytes().slice(0, buffer.size()));
                buffer = new ReleasableBytesStreamOutput(buffer.bytes().length(), bigArrays);
            }
        } finally {
            bytes.release();
        }
    }

    private void writeToFile(ByteBuffer buffer) throws IOException {
        synchronized (syncLock) {
            if (closed.get()) {
                throw new AlreadyClosedException("translog is already closed");
            }
            final long offset = totalOffset;
            totalOffset += buffer.remaining();
            channel.write(buffer);
            persistedSequenceNumberConsumer.accept(totalOffset);
        }
    }

    @Override
    public void readBytes(ByteBuffer target, long position) throws IOException {
        try {
            channel.read(target, position);
        } catch (IOException e) {
            throw new TranslogException(shardId, "failed to read bytes at position " + position, e);
        }
    }

    static void writeCheckpoint(FileChannel checkpointChannel, Path checkpointPath, Checkpoint checkpoint) throws IOException {
        try (FileChannel channel = FileChannel.open(checkpointPath, StandardOpenOption.WRITE, StandardOpenOption.CREATE_NEW)) {
            checkpoint.writeTo(channel);
            // no need to fsync here the directory sync will fsync the translog file as well since it's append only
        }
    }

    @Override
    public void close() throws IOException {
        synchronized (syncLock) {
            if (closed.compareAndSet(false, true)) {
                try {
                    if (buffer.size() > 0) {
                        writeToFile(buffer.bytes().slice(0, buffer.size()));
                    }
                    IOUtils.close(buffer);
                    channel.close();
                    writeCheckpoint(checkpointChannel, checkpointPath, new Checkpoint(totalOffset, globalCheckpointSupplier.getAsLong(),
                        minTranslogGenerationSupplier.getAsLong()));
                    checkpointChannel.close();
                } catch (IOException ex) {
                    tragedy.setTragicException(ex);
                    throw ex;
                }
            }
        }
    }
}
