            logger.severe("Failed to bind to port " + url.getPort() + ": " + e.getMessage());
            cleanup();
            return false;
        }
    }


    public void close(int timeout) {
        if (state != ChannelState.CONNECTED) {
            return;
        }

        try {
            if (serverChannel != null) {
                serverChannel.close().sync();
            }

            if (bossGroup != null) {
                bossGroup.shutdownGracefully(0, timeout, TimeUnit.MILLISECONDS).sync();
            }

            if (workerGroup != null) {
                workerGroup.shutdownGracefully(0, timeout, TimeUnit.MILLISECONDS).sync();
            }

            if (threadPoolExecutor != null) {
                threadPoolExecutor.shutdown();
                try {
                    if (!threadPoolExecutor.awaitTermination(timeout, TimeUnit.MILLISECONDS)) {
                        threadPoolExecutor.shutdownNow();
                        if (!threadPoolExecutor.awaitTermination(timeout, TimeUnit.MILLISECONDS))
                            logger.severe("ThreadPoolExecutor did not terminate");
                    }
                } catch (InterruptedException ie) {
                    threadPoolExecutor.shutdownNow();
                    Thread.currentThread().interrupt();
                }
            }
        } catch (InterruptedException e) {
            logger.severe("Interrupted during server shutdown: " + e.getMessage());
            Thread.currentThread().interrupt();
        } finally {
            cleanup();
        }
    }

    private void cleanup() {
        state = ChannelState.CLOSED;
        channelManage = null;
        bossGroup = null;
        workerGroup = null;
        serverChannel = null;
        threadPoolExecutor = null;
    }


    enum ChannelState {
        CREATED, CONNECTED, CLOSED
    }

    interface MessageHandler {
        Object handle(Object message);
    }
}

