import io.vertx.core.impl.VertxInternal;
import java.nio.ByteBuffer;
import java.nio.channels.AsynchronousFileChannel;
import java.nio.file.Paths;
import java.util.concurrent.Future;

public class AsyncFileImpl implements AsyncFile {

  private final VertxInternal vertx;
  private AsynchronousFileChannel ch;
  private final ContextInternal context;
  private boolean closed;
  private long writePos;

  private int readBufferSize = 8192;
  private Handler<Buffer> handler;
  private Handler<Void> endHandler;
  private long readPos;
  private long readLength = Long.MAX_VALUE;


  public AsyncFileImpl(VertxInternal vertx, String path, OpenOptions options, ContextInternal context) {
    this.vertx = vertx;
    this.context = context;
    try {
      this.ch = AsynchronousFileChannel.open(Paths.get(path), options.toSet());
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
  }


  @Override
  public Future<Void> close() {
    Promise<Void> promise = Promise.promise();
    closeInternal(promise);
    return promise.future();
  }

  @Override
  public Future<Buffer> read(Buffer buffer, int offset, long position, int length) {
    Promise<Buffer> promise = Promise.promise();
    check();
    ByteBuffer buff = ByteBuffer.allocate(length);
    doRead(buffer, offset, buff, position, promise);
    return promise.future();
  }

  @Override
  public Future<Void> write(Buffer buffer, long position) {
    Promise<Void> promise = Promise.promise();
    check();
    doWrite(buffer, position, promise);
    return promise.future();
  }

  private void doWrite(Buffer buffer, long position, Handler<AsyncResult<Void>> handler) {
    doWrite(buffer.getByteBuf().nioBuffers(), position, handler);
  }

  private void doWrite(ByteBuffer[] buffers, long position, Handler<AsyncResult<Void>> handler) {
    ByteBuffer first = buffers[0];
    if (first.remaining() == 0 && buffers.length == 1) {
      context.runOnContext(v -> handler.handle(Future.succeededFuture()));
      return;
    }
    doWrite(first, position, first.remaining(), handler);
  }

  private void doWrite(ByteBuffer buff, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
    writeInternal(buff, position, ar -> {
      if (ar.succeeded()) {
        long pos = position + buff.position();
        long remaining = toWrite - buff.position();
        if (remaining == 0) {
          handler.handle(Future.succeededFuture());
        } else {
          doWrite(buff, pos, remaining, handler);
        }
      } else {
        handler.handle(Future.failedFuture(ar.cause()));
      }
    });
  }

  private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
    ch.write(buff, position, null, new CompletionHandler<Integer, Object>() {
      @Override
      public void completed(Integer result, Object attachment) {
        context.runOnContext(v -> handler.handle(Future.succeededFuture()));
      }

      @Override
      public void failed(Throwable exc, Object attachment) {
        context.runOnContext(v -> handler.handle(Future.failedFuture(exc)));
      }
    });
  }

  private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
    ch.read(buff, position, null, new CompletionHandler<Integer, Object>() {
      @Override
      public void completed(Integer result, Object attachment) {
        if (result == -1) {
          context.runOnContext(v -> promise.complete(writeBuff));
        } else {
          context.runOnContext(v -> {
            buff.flip();
            writeBuff.setBytes(offset, buff);
            promise.complete(writeBuff);
          });
        }
      }

      @Override
      public void failed(Throwable exc, Object attachment) {
        context.runOnContext(v -> promise.fail(exc));
      }
    });
  }


  private void check() {
    if (closed) {
      throw new IllegalStateException("File is closed");
    }
  }

  private void closeInternal(Promise<Void> handler) {
    if (closed) {
      context.runOnContext(v -> handler.complete());
      return;
    }
    closed = true;
    try {
      ch.close();
      context.runOnContext(v -> handler.complete());
    } catch (Exception e) {
      context.runOnContext(v -> handler.fail(e));
    }
  }
}
